import React from 'react';
import ReactDOM from 'react-dom';
import './App.scss';

//https://api.themoviedb.org/3/movie/550?api_key=ccd60d653f56114f67fe5f13b2c82147
//https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        movies: [],
    }
  }
  changeHandler = () => {

  }
  getList() {
    fetch('https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147')
    .then(res => res.json())
    .then((data) => {
      this.setState({ movies: data })
      console.log(this.state.movies)
    })
    .catch(console.log)
  }
  render() {
    return (
      <div class="search-container">
        <input type="text" name="search" placeholder="Which movie you are looking for?"/>
        <button onClick={this.getList} ></button>
      </div>

    )
  }
}



class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { username: '' };
  }
  myChangeHandler = (event) => {
    this.setState({username: event.target.value});
  }
  render() {
    let header = this.state.username ? <h1>Hello {this.state.username}</h1> : '';
    return (
      <form>
      {header}
      <p>Enter your name:</p>
      <input
        type='text'
        onChange={this.myChangeHandler}
      />
      </form>
    );
  }
}

ReactDOM.render(<Input />, document.getElementById('root'));
