import React from 'react';
import ReactDOM from 'react-dom';
import './App.scss';

//https://api.themoviedb.org/3/movie/550?api_key=ccd60d653f56114f67fe5f13b2c82147
//https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        movies: [],
    }
  }
  changeHandler = () => {

  }
  getList() {
    fetch('https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147')
    .then(res => res.json())
    .then((data) => {
      this.setState({ movies: data })
      console.log(this.state.movies)
    })
    .catch(console.log)
  }
  render() {
    return (
      <div className="search-container">
        <input type="text" name="search" placeholder="Which movie you are looking for?"/>
        <button onClick={this.getList} >Go Find It!</button>
      </div>

    )
  }
}

ReactDOM.render(<Input />, document.getElementById('root'));
