import React from 'react';
import { connect } from 'react-redux';
import addMovie from './globals';
import Pagination from './pagination';
import {FAV_MOVIE_LIST, TO_WATCH_MOVIE_LIST, movieExists} from './globals';
import {changePageCount, changeIsAnyData, updateMovies, refreshMovies} from './actions';
//import store from './store';

class Table extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        //movies: [],
        //isAnyData:false,
        finalQuery: this.props.query,
        currentPage: 1,
    		//pageCount: 0,
    }
  }

  getFakeMovies = (movies) => movies.title.indexOf(this.props.query) > -1;

  getMovies() {
    let {query} = this.props;
    if(query === "") return;
    this.props.refreshMovies();
    /*fetch('https://api.themoviedb.org/3/search/movie?query='+ finalQuery + '&api_key=' + API_KEY + "&page=" + currentPage)
    .then(res => res.json())
    .then((data) => {
      movies = data.results;
      pageCount = data.total_pages;
      isAnyData = movies.length > 0;
      this.props.dispatch(updateMovies(movies));
      this.props.dispatch(changePageCount(pageCount));
      this.props.dispatch(changeIsAnyData(isAnyData));
    })
    .catch(console.log);
*/
/*    movies = fake_movies.results;
    pageCount = fake_movies.total_pages;
    isAnyData = movies.length > 0;
    this.props.updateMovies(movies.filter(this.getFakeMovies));
    this.props.changePageCount(pageCount);
    this.props.changeIsAnyData(isAnyData);
    */
  }

  componentDidMount() {
      // const cPage = Number(sessionStorage.getItem(CURRENT_PAGE));
      // this.setState({
      //   currentPage: cPage ? cPage : 1
      // }, ()=>{
      //   //this.getMovies();
      // })
      this.getMovies();

  }

  componentDidUpdate(previousProps, previousState) {
    if(previousState.finalQuery !== this.state.finalQuery) {
      this.setState({currentPage: 1}, ()=> { //get back to page number one when searching for a new movie
        this.getMovies();
      });
    }
    else if(previousState.currentPage !== this.state.currentPage){
      this.getMovies();
    }
  }

  static getDerivedStateFromProps(props, state) {
    return{
      finalQuery: props.query,
      currentPage: props.currentPage
    };
  }

  addToList(id, title, relDate, list){
    addMovie(id, title, relDate, list);
    this.forceUpdate();
  }

  render(){
    if(this.props.isAnyData)
      return (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.props.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td className="icon"><i className={(movieExists(row.id, FAV_MOVIE_LIST) ? "fas" : "far") + " fa-star"} onClick={()=>{this.addToList(row.id, row.title, row.release_date, FAV_MOVIE_LIST)}}></i></td>
                <td className="icon"><i className={movieExists(row.id, TO_WATCH_MOVIE_LIST) ? "fas fa-eye" : "far fa-eye-slash"} onClick={()=>{this.addToList(row.id, row.title, row.release_date, TO_WATCH_MOVIE_LIST)}}></i></td>
              </tr>
            ))}
            </tbody>
          </table>
          <Pagination />
        </div>

      );
    else {
      return (
        <div className="table-container">
          <h2 className="no-results no-selection">Nothing To Watch!</h2>
        </div>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    movies: state.movies,
    pageCount: state.pageCount,
    isAnyData: state.isAnyData,
    currentPage: state.currentPage,
    query: state.query
  };
}

const mapDispatchToProps = {
  changePageCount,
  changeIsAnyData,
  updateMovies,
  refreshMovies
};

export default connect(mapStateToProps, mapDispatchToProps)(Table);
