export const CHANGE_PAGE = "changePage";
export const UPDATE_MOVIES = "updateMovies";
export const ADD_TO_FAV = "addToFavourites";
export const ADD_TO_WATCH = "addToWatchList";

export function changePage(pageNum) {
   return {
      type: CHANGE_PAGE,
      pageNum: pageNum
   }
}

export function addToFavourites(movie) {
   return {
      type: ADD_TO_FAV,
      movie: movie
   }
}

export function addToWatchList(movie) {
   return {
      type: ADD_TO_WATCH,
      movie: movie
   }
}
