import React from 'react';
import { connect } from 'react-redux';
import addMovie from './globals';
import Pagination from './pagination';
import {FAV_MOVIE_LIST, TO_WATCH_MOVIE_LIST, API_KEY, movieExists} from './globals';
import {changePageCount, changeIsAnyData, updateMovies} from './actions';
import store from './store';
import fake_movies from './movies';

class Table extends React.Component {
/*  constructor(props){
    super(props);
    this.state = {
        movies: [],
        isAnyData:false,
        finalQuery: this.props.query,
        currentPage: 1,
    		pageCount: 0,
    }
  }*/

  getFakeMovies = (movies) => movies.title.indexOf(this.props.query) > -1;

  getMovies() {
    let {query, movies, pageCount, isAnyData} = this.props;
    if(query === "") return;

    /*fetch('movies.json')//'https://api.themoviedb.org/3/search/movie?query='+ finalQuery + '&api_key=' + API_KEY + "&page=" + currentPage)
    .then(res => res.json())
    .then((data) => {
      movies = data.results;
      pageCount = data.total_pages;
      isAnyData = movies.length > 0;
      store.dispatch(updateMovies(movies));
      store.dispatch(changePageCount(pageCount));
      store.dispatch(changeIsAnyData(isAnyData));
    })
    .catch(console.log);
*/
    movies = fake_movies.results;
    pageCount = fake_movies.total_pages;
    isAnyData = movies.length > 0;
    store.dispatch(updateMovies(movies.filter(this.getFakeMovies)));
    store.dispatch(changePageCount(pageCount));
    store.dispatch(changeIsAnyData(isAnyData));
    console.log(this.props);
  }

  componentDidMount() {
      // const cPage = Number(sessionStorage.getItem(CURRENT_PAGE));
      // this.setState({
      //   currentPage: cPage ? cPage : 1
      // }, ()=>{
      //   //this.getMovies();
      // })
      console.log(this.props);
      this.getMovies();

  }

  componentDidUpdate(previousProps, previousState) {
    /*if(previousState.finalQuery !== this.state.finalQuery) {
      this.setState({currentPage: 1}, ()=> { //get back to page number one when searching for a new movie
        this.getMovies();
      });
    }
    else if(previousState.currentPage !== this.state.currentPage){*/
      //this.getMovies();
    //}
  }

  /*static getDerivedStateFromProps(props, state) {
    return{finalQuery: props.query};
  }*/

  addToList(id, title, relDate, list){
    addMovie(id, title, relDate, list);
    this.forceUpdate();
  }

  render(){
    console.log("render");
    if(this.props.isAnyData)
      return (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.props.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td className="icon"><i className={(movieExists(row.id, FAV_MOVIE_LIST) ? "fas" : "far") + " fa-star"} onClick={()=>{this.addToList(row.id, row.title, row.release_date, FAV_MOVIE_LIST)}}></i></td>
                <td className="icon"><i className={movieExists(row.id, TO_WATCH_MOVIE_LIST) ? "fas fa-eye" : "far fa-eye-slash"} onClick={()=>{this.addToList(row.id, row.title, row.release_date, TO_WATCH_MOVIE_LIST)}}></i></td>
              </tr>
            ))}
            </tbody>
          </table>
          <Pagination />
        </div>

      );
    else {
      return (
        <div className="table-container">
          <h2 className="no-results no-selection">Nothing To Watch!</h2>
        </div>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    finalQuery: state.finalQuery,
    movies: state.movies,
    pageCount: state.pageCount,
    isAnyData: state.isAnyData,
    query: state.query
  };
}

export default connect(mapStateToProps)(Table);
