import React from 'react';
import {QUERY} from './globals';

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      movieQuery: "",
    }
  }

  componentDidMount() {
    this.setState({movieQuery: localStorage.getItem(QUERY)});

  }

  changeHandler = (event) => {
    this.setState ({
      movieQuery: event.target.value
    });
  }
  sendQuery = (e) => {
    if (e.keyCode === undefined || e.keyCode === 13)
      this.props.parentCallback(this.state.movieQuery);
  }

  render() {
    return (
      <div className="search-container">
        <input type="text" name="search" placeholder="Which movie you are looking for?" onChange={(e) => {this.changeHandler(e)}} onKeyDown={(e)=>{this.sendQuery(e)}} />
        <button onClick={this.sendQuery} >Find My Movie!</button>
      </div>

    )
  }
}

export default Search;
