import React from 'react';
import PropTypes from 'prop-types'

class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: this.props.currentPage,
      postsPerPage: 20, //It has been set from the used API, Nothing I can modify
      pageCount: this.props.pageCount,
      pageNumbers2Display: 5, //Set an odd number > 1
    }
  }


  render(){

    const { currentPage, postsPerPage, pageCount, pageNumbers2Display } = this.state;
    //for pages less than twice as mush as pageNumbers2Display, display all the page numbers,
    //cause its pointless to show extra buttons in this case scenarios
    const shouldDisplayExtraButtons = pageCount - pageNumbers2Display >= pageNumbers2Display;
    function getPageNumberList() {
      const list = [];
      if(shouldDisplayExtraButtons) {
        let i = currentPage <= pageNumbers2Display ? 1 :
              currentPage + pageNumbers2Display > pageCount ? pageCount - (pageNumbers2Display - 1) :
                currentPage - (Math.floor(pageNumbers2Display / 2));
        let toNumber = i + pageNumbers2Display;
        for (i; i < toNumber; i++) {
          list.push(i);
        }
      }
      else {
        for (let i = 1; i <= Math.ceil(pageCount); i++) {
          list.push(i);
        }
      }
      return list;
    }

    function PrevPage() {
      return (
        <li key="p-page">
          <a href="#">&lt;</a>
        </li>
      )
    }
    // /        {/* onClick: this.changePage */}
    function NextPage() {
      return (
        <li key="n-page">
          <a href="#">&gt;</a>
        </li>
      )
    }

    function RenderPageNumbers() {
      return (
        getPageNumberList().map(number => {
         return (
           <li className={currentPage === number ? 'active' : ''} key={number} id={number}>
             <a href="#">{number}</a>
           </li>
         );
       })
      );
    }

    class FirstPage extends React.Component {
		  render() {

			if((currentPage > pageNumbers2Display) && shouldDisplayExtraButtons) {

        return (
          <li className = {currentPage === 1 ? ' active' : ''} key="f-page" onclick ={this.props.changeP} >
            <a href="#">1</a>
          </li>
        )
			}
			else
				return null;
		  }
		}

		FirstPage.propTypes = {
		  changeP: PropTypes.func
		};

    class LastPage extends React.Component {
		  render() {
  			if((currentPage < pageCount - (pageNumbers2Display - 1)) && shouldDisplayExtraButtons) {
          return (
            <li className = {currentPage === pageCount ? ' active' : ''} key="l-page" onclick ={this.props.changeP} >
              <a href="#">{pageCount}</a>
            </li>
          )
  			}
  			else
  				return null;
		  }
		}

		LastPage.propTypes = {
		  changeP: PropTypes.func
		};

    class StartDots extends React.Component {
			render() {
				if((currentPage > pageNumbers2Display) && shouldDisplayExtraButtons) {
          return (
            <li key="sdots" onclick ={this.props.changeP} >
              <a href="#">...</a>
            </li>
          )
				}
				else
					return null;
			}
		}
		StartDots.propTypes = {
			changeP: PropTypes.func
		}

		class EndDots extends React.Component {
			render() {
				if((currentPage < pageCount - (pageNumbers2Display - 1)) && shouldDisplayExtraButtons) {
          return (
            <li key="edots" onclick ={this.props.changeP} >
              <a href="#">...</a>
            </li>
          )
				}
				else
					return null;
			}
		}
		EndDots.propTypes = {
			changeP: PropTypes.func
		}

    if(this.state.pageCount > 1) {
      return (
        <div className="row">
          <ul className="pagination">
            <PrevPage />
            <FirstPage changeP={this.props.changeP} />
            <StartDots changeP={this.props.changeP} />
            <RenderPageNumbers />
            <EndDots changeP={this.props.changeP} />
            <LastPage changeP={this.props.changeP} />
            <NextPage />
          </ul>
        </div>
      );
    }
    else
      return null;
  }
}

export default Pagination;
