const CHANGE_PAGE = "CHANGE_PAGE";
const UPDATE_MOVIES = "UPDATE_MOVIES";
const ADD_TO_FAV = "ADD_TO_FAV";
const ADD_TO_WATCH = "ADD_TO_WATCH";
const CHANGE_QUERY = "CHANGE_QUERY";
export const REFRESH_MOVIES = "REFRESH_MOVIES";

export function changePage(pageNum) {
   return {
      type: CHANGE_PAGE,
      pageNum: pageNum
   }
}

export function updateMovies(movies) {
   return {
      type: UPDATE_MOVIES,
      movies: movies
   }
}

export function changeQuery(query) {
   return {
      type: CHANGE_QUERY,
      query: query
   }
}

export function addToFavourites(movie) {
   return {
      type: ADD_TO_FAV,
      movie: movie
   }
}

export function addToWatchList(movie) {
   return {
      type: ADD_TO_WATCH,
      movie: movie
   }
}

export function refreshMovies() {
  return {
    type: REFRESH_MOVIES
  }
}
