import {CHANGE_PAGE, UPDATE_MOVIES, ADD_TO_FAV, ADD_TO_WATCH, IS_ANY_DATA, CHANGE_PAGE_COUNT, CHANGE_QUERY} from './actions';

const initialState = {
  movies: [],
  isAnyData:false,
  query: "",
  currentPage: 1,
  pageCount: 0,
  postsPerPage: 20, //It has been set from the used API, Nothing I can modify
  pageNumbers2Display: 5, //Set an odd number > 1
};

const changePage = (state, action) => Object.assign({},state,{
  currentPage: action.pageNum
});

const changePageCount = (state, action) => Object.assign({},state,{
  pageCount: action.pageCount
});

const changeIsAnyData = (state, action) => Object.assign({},state,{
  isAnyData: action.isAnyData
});

const updateMovies = (state, action) => Object.assign({},state,{
  movies: action.movies
});

const changeQuery = (state, action) => Object.assign({},state,{
  query: action.query
});

export default function reducer(state = initialState, action) {
  switch(action.type) {
    case CHANGE_PAGE:
      return changePage(state, action);
    case CHANGE_PAGE_COUNT:
      return changePageCount(state, action);
    case IS_ANY_DATA:
      return changeIsAnyData(state, action);
      case IS_ANY_DATA:
        return changeIsAnyData(state, action);
    case UPDATE_MOVIES:
      return updateMovies(state, action);
    case ADD_TO_FAV:
    case ADD_TO_WATCH:
      return state;
      // return {
      //   count: state.count + 1
      // };
    default:
      return state;
  }
}
