import React from 'react';
import ReactDOM from 'react-dom';
import './css/all.min.css';
import './css/fontawesome.min.css';
import './scss/App.scss';
// import './styles.css';

//https://api.themoviedb.org/3/movie/550?api_key=ccd60d653f56114f67fe5f13b2c82147
//https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147
//https://api.themoviedb.org/3/search/movie?query=avengers&api_key=ccd60d653f56114f67fe5f13b2c82147&page=2

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      movieQuery: "",
    }
  }
  changeHandler = (event) => {
    this.setState ({
      movieQuery: event.target.value
    });
  }
  sendQuery = () => {
    this.props.parentCallback(this.state.movieQuery);
  }
  render() {
    return (
      <div className="search-container">
        <input type="text" name="search" placeholder="Which movie you are looking for?" onChange={(e) => {this.changeHandler(e)}}/>
        <button onClick={this.sendQuery} >Go Find It!</button>
      </div>

    )
  }
}

class Table extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        movies: [],
        isAnyData:true,
        finalQuery: this.props.query,
    }
  }

  getMovies() {
    fetch('https://api.themoviedb.org/3/search/movie?query='+ this.state.finalQuery + '&api_key=ccd60d653f56114f67fe5f13b2c82147')
    .then(res => res.json())
    .then((data) => {
      this.setState({ movies: data.results,isAnyData : this.state.movies.length > 0 });
    })
    .catch(console.log);
  }

  componentDidMount() {
    this.getMovies();
  }

  componentDidUpdate(previousProps, previousState) {
    if(previousState.finalQuery !== this.state.finalQuery)
      this.getMovies();
  }
  static getDerivedStateFromProps(props, state) {
    return{finalQuery: props.query};
  }

  render(){
    if(this.state.isAnyData)
      return (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.state.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td className="icon"><i className="far fa-star"></i></td>
                <td className="icon"><i className="far fa-eye-slash"></i></td>
              </tr>

            ))}
            </tbody>
          </table>
        </div>
      );
    else {
      return <div className="table-container">No Results!</div>;
    }
  }
}

class Container extends React.Component {
  constructor(props) {
    super(props);
    this.child = React.createRef();
    this.state = {
      parentQuery:"",
    }
  }

  getMovieQuery = (childData) => {
    this.setState({parentQuery: childData});
  }
  render() {
    return (
      <div className="container">
        <Search parentCallback = {this.getMovieQuery}/>
        <Table query={this.state.parentQuery} />
      </div>
    );
  }
}

ReactDOM.render(<Container />, document.getElementById('root'));
