import React from 'react';
import addMovie from './globals';
import RealTable from './real_table'
import {FAV_MOVIE_LIST, TO_WATCH_MOVIE_LIST, movieExists} from './globals';

class Favorites extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      movies: localStorage.getItem(FAV_MOVIE_LIST) ? JSON.parse(localStorage.getItem(FAV_MOVIE_LIST)) : [],
    }
  }

  render() {
    return(
      <div className="container">
        <RealTable movies={this.state.movies} hasPagination={false}/>
      </div>
    )
  }
}

export default Favorites;
