import React from 'react';

class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: this.props.currentPage,
      postsPerPage: 20, //It has been set from the used API, Nothing I can modify
      pageCount: this.props.pageCount,
      pageNumbers2Display: 5, //Set an odd number > 1
    }
    this.handleClick = this.handleClick.bind(this);
    this.changePage = this.changePage.bind(this);
  }

  //Handle click on page number list
  handleClick(event) {
    this.setState({
      currentPage: Number(event.currentTarget.id) //using event.target would reference to child tag (a). So use event.currentTarget
    });
    event.preventDefault();
  }

  //Handle next and previous buttons
  changePage (event) {
    event.preventDefault();
    let newPage = 1;
    let extraPages2Jump = 3;
    let page2JumpFrom;
    switch (event.currentTarget.id){
      case 'p-page'://previous
        newPage = this.state.currentPage > 1 ? this.state.currentPage - 1 : 1;
      break;
      case 'n-page'://next
        newPage = this.state.currentPage < this.state.pageCount ? this.state.currentPage + 1 : this.state.pageCount;
      break;
      case 'f-page'://first
        newPage = 1;
      break;
      case 'l-page'://last
        newPage = this.state.pageCount;
      break;
      case 'e-dots'://last dots
        page2JumpFrom = this.state.currentPage <= this.state.pageNumbers2Display ? this.state.pageNumbers2Display : this.state.currentPage + 2;
        newPage = page2JumpFrom + extraPages2Jump <= this.state.pageCount ? page2JumpFrom + extraPages2Jump : this.state.currentPage;
      break;
      default: //first dots 'f-dots'
        page2JumpFrom = this.state.currentPage >= this.state.pageCount - (this.state.pageNumbers2Display - 1) ? this.state.pageCount - (this.state.pageNumbers2Display - 1) :  this.state.currentPage - 2;
        newPage = page2JumpFrom - 3 >= 1 ? page2JumpFrom - 3 : 1;
    }
    this.setState({
      currentPage: newPage
    });
  }

  prevPage() {
    if(this.currentPage > 1)
      return (
        <li key="p-page" id="p-page" onClick={this.changePage}>
          <a href="#">&lt;</a>
        </li>
      )
    else {
      return null;
    }
  }

  nextPage() {
    return (
      <li key="n-page" id="n-page" onClick={this.changePage}>
        <a href="#">&gt;</a>
      </li>
    )
  }

  render(){

    const { currentPage, postsPerPage, pageCount, pageNumbers2Display } = this.state;
    //for pages less than twice as mush as pageNumbers2Display, display all the page numbers,
    //cause its pointless to show extra buttons in this case scenarios
    const shouldDisplayExtraButtons = pageCount - pageNumbers2Display >= pageNumbers2Display;
    function getPageNumberList() {
      const list = [];
      if(shouldDisplayExtraButtons) {
        let i = currentPage <= pageNumbers2Display ? 1 :
              currentPage + pageNumbers2Display > pageCount ? pageCount - (pageNumbers2Display - 1) :
                currentPage - (Math.floor(pageNumbers2Display / 2));
        let toNumber = i + pageNumbers2Display;
        for (i; i < toNumber; i++) {
          list.push(i);
        }
      }
      else {
        for (let i = 1; i <= Math.ceil(pageCount); i++) {
          list.push(i);
        }
      }
      return list;
    }

    const RenderPageNumbers = () => {
      return (
        getPageNumberList().map(number => {
         return (
           <li className={currentPage === number ? 'active' : ''} key={number} id={number} onClick={this.handleClick}>
             <a href="#">{number}</a>
           </li>
         );
       })
      );
    }

    const FirstPage = () => {
      if((currentPage > pageNumbers2Display) && shouldDisplayExtraButtons) {
        return (
          <li className = {currentPage === 1 ? ' active' : ''} key="f-page" id="f-page" onClick ={this.changePage} >
            <a href="#">1</a>
          </li>
        )
			}
			else
				return null;
    }

    const LastPage = () => {
  			if((currentPage < pageCount - (pageNumbers2Display - 1)) && shouldDisplayExtraButtons) {
          return (
            <li className = {currentPage === pageCount ? ' active' : ''} key="l-page" id="l-page" onClick ={this.changePage} >
              <a href="#">{pageCount}</a>
            </li>
          )
  			}
  			else
  				return null;
		}

    const StartDots = () => {
      if((currentPage > pageNumbers2Display) && shouldDisplayExtraButtons) {
        return (
          <li key="sdots" id="s-dots" onClick ={this.changePage} >
            <a href="#">...</a>
          </li>
        )
      }
      else
        return null;
    }

    const EndDots = () => {
      if((currentPage < pageCount - (pageNumbers2Display - 1)) && shouldDisplayExtraButtons) {
        return (
          <li key="edots" id="e-dots" onClick ={this.changePage} >
            <a href="#">...</a>
          </li>
        )
      }
      else
        return null;
    }

    if(this.state.pageCount > 1) {
      return (
        <div className="row">
          <ul className="pagination">
            {this.prevPage()}
            <FirstPage />
            <StartDots />
            <RenderPageNumbers />
            <EndDots />
            <LastPage />
            {this.nextPage()}
          </ul>
        </div>
      );
    }
    else
      return null;
  }
}

export default Pagination;
