import { put, takeLatest, all } from 'redux-saga/effects';
import {API_KEY} from './globals';


function* fetchNews() {  const json = yield fetch('https://api.themoviedb.org/3/search/movie?query='+ finalQuery + '&api_key=' + API_KEY + "&page=" + currentPage)
        .then(response => response.json(), );      yield put({ type: "NEWS_RECEIVED", json: json.articles, });
}

function* actionWatcher() {
     yield takeLatest('GET_NEWS', fetchNews)
}

export default function* rootSaga() {
   yield all([
   actionWatcher(),
   ]);
}
