import React from 'react';

class Favorites extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
        <h1>Favorites</h1>
    );
  }
}

function addToFavortite(movie) {
  let movieArray = localStorage.getItem('faveMovieList') ? JSON.parse(localStorage.getItem('faveMovieList')) : [];
  if(isMovieInFavorites(movie.id)) return;
  movieArray.push(movie);
  localStorage.setItem('faveMovieList', JSON.stringify(movieArray));
}

function isMovieInFavorites(id){
  let movieArray = localStorage.getItem('faveMovieList') ? JSON.parse(localStorage.getItem('faveMovieList')) : [];
  movieArray.forEach((movie)=>{
    if(movie.id == id)
      return true;
  });
  return false;
}

export {Favorites, addToFavortite};
