import { put, takeLatest, all } from 'redux-saga/effects';
import {API_KEY} from './globals';
import {UPDATE_MOVIES, REFRESH_MOVIES} from './actions';


function* fetchNews() {
  const json = yield fetch('https://api.themoviedb.org/3/search/movie?query=a&api_key=ccd60d653f56114f67fe5f13b2c82147&page=7')//'https://api.themoviedb.org/3/search/movie?query='+ finalQuery + '&api_key=' + API_KEY + "&page=" + currentPage)
        .then(response => response.json() );
  yield put({ type: UPDATE_MOVIES, json: json, });
}

function* actionWatcher() {
  yield takeLatest(REFRESH_MOVIES, fetchNews)
}

export default function* rootSaga() {
 yield all([
 actionWatcher(),
 ]);
}
