import React from 'react';

class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: this.props.currentPage,
      postsPerPage: 20, //It has been set from the used API, Nothing I can modify
      pageCount: this.props.pageCount,
      pageNumbers2Display: 5, //Set an odd number > 1
    }
  }


  render(){

    const { currentPage, postsPerPage, pageCount, pageNumbers2Display } = this.state;
    //for pages less than twice as mush as pageNumbers2Display, display all the page numbers,
    //cause its pointless to show extra buttons in this case scenarios
    const shouldDisplayExtraButtons = pageCount - pageNumbers2Display >= pageNumbers2Display;

    function getPageNumberList() {
      const list = [];
      if(shouldDisplayExtraButtons) {
        let i = currentPage <= pageNumbers2Display ? 1 :
              currentPage + pageNumbers2Display > pageCount ? pageCount - (pageNumbers2Display - 1) :
                currentPage - (Math.floor(pageNumbers2Display / 2));
        let toNumber = i + pageNumbers2Display;
        for (i; i < toNumber; i++) {
          list.push(i);
        }
      }
      else {
        for (let i = 1; i <= Math.ceil(pageCount); i++) {
          list.push(i);
        }
      }
      return list;
    }

    // const RenderPageNumbers = getPageNumberList().map(number => {
    //   return (
    //     <li className={currentPage === number ? 'active' : ''} key={number} id={number}>
    //       <a href="#">number</a>
    //     </li>
    //   );
    // });

    if(this.state.pageCount > 1) {
      return (
        <div className="row">
          <ul className="pagination">
            <RenderPageNumbers currentPage={currentPage} number={number}/>
          </ul>
        </div>
      );
    }
    else
      return null;
  }
}

class RenderPageNumbers extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      currentPage : this.props.currentPage,
      number : this.props.number,
    }
  }

  render() {
    return (
      <li className={this.state.currentPage === this.state.number ? 'active' : ''} key={this.state.number} id={this.state.number}>
        <a href="#">{this.state.number}</a>
      </li>
    );
  }
}

export default Pagination;
