import React from 'react';
import Search from './search';
import Table from './main-table';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.child = React.createRef();
    this.state = {
      parentQuery:"",
    }
  }

  getMovieQuery = (childData) => {
    this.setState({parentQuery: childData});
  }
  render() {
    return (
      <div className="container">
        <Search parentCallback = {this.getMovieQuery}/>
        <Table query={this.state.parentQuery}/>
      </div>
    );
  }
}

export default App;
