import React from 'react';
import ReactDOM from 'react-dom';

export class Table extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        movies: [],
        isAnyData:true,
        finalQuery: this.props.query,
    }
  }

  getMovies() {
    if(this.state.finalQuery === "") return;
    fetch('https://api.themoviedb.org/3/search/movie?query='+ this.state.finalQuery + '&api_key=ccd60d653f56114f67fe5f13b2c82147')
    .then(res => res.json())
    .then((data) => {
      this.setState({movies: data.results});
      this.setState({isAnyData : this.state.movies.length > 0 });
    })
    .catch(console.log);
  }

  componentDidMount() {
    this.getMovies();
  }

  componentDidUpdate(previousProps, previousState) {
    if(previousState.finalQuery !== this.state.finalQuery)
      this.getMovies();
  }
  static getDerivedStateFromProps(props, state) {
    return{finalQuery: props.query};
  }

  render(){
    if(this.state.isAnyData)
      return (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.state.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td className="icon"><i className="far fa-star"></i></td>
                <td className="icon"><i className="far fa-eye-slash"></i></td>
              </tr>

            ))}
            </tbody>
          </table>
        </div>
      );
    else {
      return <div className="table-container">No Results!</div>;
    }
  }
}
