import {CHANGE_PAGE, UPDATE_MOVIES, ADD_TO_FAV, ADD_TO_WATCH} from './actions';

const initialState = {
  movies: [],
  isAnyData:false,
  finalQuery: "",
  currentPage: 1,
  pageCount: 0,
  postsPerPage: 20, //It has been set from the used API, Nothing I can modify
  pageNumbers2Display: 5, //Set an odd number > 1
};

const changePage = (state, action) => Object.assign({},state,{
  currentPage: action.pageNum
});

export default function reducer(state = initialState, action) {
  switch(action.type) {
    case CHANGE_PAGE:
      return changePage(state, action);
    case UPDATE_MOVIES:
    case ADD_TO_FAV:
    case ADD_TO_WATCH:
      return state;
      // return {
      //   count: state.count + 1
      // };
    default:
      return state;
  }
}
