import React from 'react';
import addMovie from './globals';
import Pagination from './pagination';
import {FAV_MOVIE_LIST, TO_WATCH_MOVIE_LIST, API_KEY, movieExists} from './globals';

class Table extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        movies: [],
        isAnyData:false,
        finalQuery: this.props.query,
        currentPage: 1,
    		pageCount: 0,
    }
  }

  getMovies() {
    if(this.state.finalQuery === "") return;
    fetch('https://api.themoviedb.org/3/search/movie?query='+ this.state.finalQuery + '&api_key=' + API_KEY + "&page=" + this.state.currentPage)
    .then(res => res.json())
    .then((data) => {
      this.setState({movies: data.results, pageCount:data.total_pages}, ()=>{
        this.setState({isAnyData : this.state.movies.length > 0 });
      });

    })
    .catch(console.log);
  }

  componentDidMount() {
    this.getMovies();
  }

  componentDidUpdate(previousProps, previousState) {
    if(previousState.finalQuery !== this.state.finalQuery || previousState.currentPage !== this.state.currentPage)
      this.getMovies();
  }
  static getDerivedStateFromProps(props, state) {
    return{finalQuery: props.query};
  }

  addToList(id, title, relDate, list){
    addMovie(id, title, relDate, list);
    this.forceUpdate();
  }

  changePage = (newPage) => {
    this.setState({currentPage: newPage});

  }

  render(){
    if(this.state.isAnyData)
      return (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.state.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td className="icon"><i className={(movieExists(row.id, FAV_MOVIE_LIST) ? "fas" : "far") + " fa-star"} onClick={()=>{this.addToList(row.id, row.title, row.release_date, FAV_MOVIE_LIST)}}></i></td>
                <td className="icon"><i className={movieExists(row.id, TO_WATCH_MOVIE_LIST) ? "fas fa-eye" : "far fa-eye-slash"} onClick={()=>{this.addToList(row.id, row.title, row.release_date, TO_WATCH_MOVIE_LIST)}}></i></td>
              </tr>
            ))}
            </tbody>
          </table>
          <Pagination currentPage={this.state.currentPage} pageCount={this.state.pageCount} parentCallback = {this.changePage}/>
        </div>

      );
    else {
      return (
        <div className="table-container">
          <h2 className="no-results no-selection">Nothing To Watch!</h2>
        </div>
      );
    }
  }
}

export default Table;
