export const CHANGE_PAGE = "changePage";
export const UPDATE_MOVIES = "updateMovies";
export const ADD_TO_FAV = "addToFavourites";
export const ADD_TO_WATCH = "addToWatchList";
export const IS_ANY_DATA = "isAnyData";
export const CHANGE_PAGE_COUNT = "changePageCount";
export const CHANGE_QUERY = "changeQuery";

export function changePage(pageNum) {
   return {
      type: CHANGE_PAGE,
      pageNum: pageNum
   }
}

export function changePageCount(pageCount) {
   return {
      type: CHANGE_PAGE_COUNT,
      pageCount: pageCount
   }
}

export function changeIsAnyData(isAnyData) {
   return {
      type: IS_ANY_DATA,
      isAnyData: isAnyData
   }
}

export function updateMovies(movies) {
   return {
      type: UPDATE_MOVIES,
      movies: movies
   }
}

export function changeQuery(query) {
   return {
      type: CHANGE_QUERY,
      query: query
   }
}

export function addToFavourites(movie) {
   return {
      type: ADD_TO_FAV,
      movie: movie
   }
}

export function addToWatchList(movie) {
   return {
      type: ADD_TO_WATCH,
      movie: movie
   }
}
