import React from 'react';
import ReactDOM from 'react-dom';
import './css/all.min.css';
import './css/fontawesome.min.css';
import './scss/App.scss';
// import './styles.css';

//https://api.themoviedb.org/3/movie/550?api_key=ccd60d653f56114f67fe5f13b2c82147
//https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {


    }
  }
  changeHandler = () => {

  }
  getList = () => {

  }
  render() {
    return (
      <div className="search-container">
        <input type="text" name="search" placeholder="Which movie you are looking for?"/>
        <button onClick={this.getList} >Go Find It!</button>
      </div>

    )
  }
}

class Table extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        movies: [],
        isAnyData:true,
    }
  }

  componentDidMount() {
    fetch('https://api.themoviedb.org/3/list/10?api_key=ccd60d653f56114f67fe5f13b2c82147')
    .then(res => res.json())
    .then((data) => {
      this.setState({ movies: data.items })
      console.log(this.state.movies)
    })
    .catch(console.log)
  }

  render(){
    if(this.state.isAnyData)
      return (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.state.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td><i class="far fa-star"></i></td>
                <td><i class="far fa-eye-slash"></i></td>
              </tr>

            ))}
            </tbody>
          </table>
        </div>
      );
    else {
      return <div className="table-container">No Results!</div>;
    }
  }
}

class Container extends React.Component {
  constructor() {
    super();
    this.state = {

    }
  }

  render() {
    return (
      <div className="container">
        <Search />
        <Table />
      </div>
    );
  }
}

ReactDOM.render(<Container />, document.getElementById('root'));
