import React from 'react';
import Movie from './movie';

class Favorites extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      movies: [],
    }
  }

  componentDidMount(){
    this.setState({
      movies: localStorage.getItem('faveMovieList') ? JSON.parse(localStorage.getItem('faveMovieList')) : [],
    });
  }

  addFavMovie(id, title, relDate){
    addToFavortite(id, title, relDate);
    this.forceUpdate();
  }

  render() {
    return (
      <div className="container">
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Release Date</th>
                <th>Favorite</th>
                <th>To Watch</th>
              </tr>
            </thead>
            <tbody>
            {this.state.movies.map((row) => (
              <tr key={row.id}>
                <td>{row.title}</td>
                <td>{row.release_date}</td>
                <td className="icon"><i className={(isMovieInFavorites(row.id) ? "fas" : "far") + " fa-star"} onClick={()=>{this.addFavMovie(row.id, row.title, row.release_date)}}></i></td>
                <td className="icon"><i className="far fa-eye-slash"></i></td>
              </tr>
            ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

function addToFavortite(id, title, relDate) {
  let movie = new Movie(id, title, relDate);
  let movieArray = localStorage.getItem('faveMovieList') ? JSON.parse(localStorage.getItem('faveMovieList')) : [];
  if(isMovieInFavorites(movie.id)) {
    movieArray = removeFromArray(movieArray, movie.id);
  }
  else{
    movieArray.push(movie);
  }
  localStorage.setItem('faveMovieList', JSON.stringify(movieArray));
}


function removeFromArray(arr, id) {
   return arr.filter(function(item){
       return item.id !== id;
   });
}

function isMovieInFavorites(id){
  let movieArray = localStorage.getItem('faveMovieList') ? JSON.parse(localStorage.getItem('faveMovieList')) : [];
  return movieArray.some(movie => movie.id === id)
}

export {Favorites as default, addToFavortite, isMovieInFavorites};
